void acts_all::MEMACCESSP2_readVchunks(bool_type enable, uint512_dt * kvdram, keyvalue_vbuffer_t buffer[VDATA_PACKINGSIZE][BLOCKRAM_VDATA_SIZE], batch_type vbaseoffset_kvs, batch_type voffset_kvs, batch_type vsz_kvs, globalparams_t globalparams){
	// read vertices data
	unsigned int depth = globalparams.NUM_REDUCEPARTITIONS * globalparams.SIZEKVS2_REDUCEPARTITION;
	unsigned int bdepth = vsz_kvs / 2;
	unsigned int depth_i = 0;
	unsigned int bdepth_i = 0;
	keyvalue_vbuffer_t tempbuffer[VDATA_PACKINGSIZE][BLOCKRAM_VDATA_SIZE]; // CRITICAL REMOVEME?
	#pragma HLS array_partition variable = tempbuffer
	READVDATACHUNKS_LOOP1: for(unsigned int s=0; s<NUM_PEs; s++){ 
		#ifdef _DEBUGMODE_KERNELPRINTS
		cout<<"MEMACCESSP2_readVchunks:: size loaded @ s("<<s<<"): offset_kvs2: "<<(s * vsz_kvs)<<", sz_kvs2: "<<vsz_kvs<<endl;
		#endif
		MEMACCESSP2_SLreadV(enable, s, kvdram, tempbuffer, vbaseoffset_kvs, depth_i + voffset_kvs, bdepth_i, vsz_kvs, globalparams);
		depth_i += depth;
		bdepth_i += bdepth;
	}
	
	// re-arrange vertices data 
	vmdata_t vandmdata1[VECTOR2_SIZE];
	vmdata_t vandmdata2[VECTOR2_SIZE];
	#pragma HLS ARRAY_PARTITION variable=vandmdata1 complete
	#pragma HLS ARRAY_PARTITION variable=vandmdata2 complete
	vmdata_t vandmdataM[VECTOR2_SIZE]; 
	vmdata_t vandmdataN[VECTOR2_SIZE]; 
	keyvalue_vbuffer_t vsandmsdata[VECTOR2_SIZE];
	#pragma HLS ARRAY_PARTITION variable=vandmdataM complete
	#pragma HLS ARRAY_PARTITION variable=vandmdataN complete
	READVDATACHUNKS_REARRANGE_LOOP1: for(unsigned int i=0; i<DOUBLE_BLOCKRAM_SIZE; i+=2){
	#pragma HLS PIPELINE II=1
		READVDATACHUNKS_REARRANGE_LOOP1B: for(unsigned int j=0; j<2; j++){
			unsigned int index = i+j;
			MEMCAP2_READFROMBUFFER_VDATASANDVMASKS(index, tempbuffer, vandmdata1, 0);	
			MEMACCESSP2_RearrangeLayoutV(voffset_kvs + index, vandmdata1, vandmdata2);
			if(j==0){ vandmdataM[0] = vandmdata2[0];vandmdataM[1] = vandmdata2[1];vandmdataM[2] = vandmdata2[2];vandmdataM[3] = vandmdata2[3];vandmdataM[4] = vandmdata2[4];vandmdataM[5] = vandmdata2[5];vandmdataM[6] = vandmdata2[6];vandmdataM[7] = vandmdata2[7];vandmdataM[8] = vandmdata2[8];vandmdataM[9] = vandmdata2[9];vandmdataM[10] = vandmdata2[10];vandmdataM[11] = vandmdata2[11];vandmdataM[12] = vandmdata2[12];vandmdataM[13] = vandmdata2[13];vandmdataM[14] = vandmdata2[14];vandmdataM[15] = vandmdata2[15];} else { vandmdataN[0] = vandmdata2[0];vandmdataN[1] = vandmdata2[1];vandmdataN[2] = vandmdata2[2];vandmdataN[3] = vandmdata2[3];vandmdataN[4] = vandmdata2[4];vandmdataN[5] = vandmdata2[5];vandmdataN[6] = vandmdata2[6];vandmdataN[7] = vandmdata2[7];vandmdataN[8] = vandmdata2[8];vandmdataN[9] = vandmdata2[9];vandmdataN[10] = vandmdata2[10];vandmdataN[11] = vandmdata2[11];vandmdataN[12] = vandmdata2[12];vandmdataN[13] = vandmdata2[13];vandmdataN[14] = vandmdata2[14];vandmdataN[15] = vandmdata2[15];	  }
			
			if(j==1){
				vsandmsdata[0] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[0], vandmdataN[0]);
				vsandmsdata[1] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[1], vandmdataN[1]);
				vsandmsdata[2] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[2], vandmdataN[2]);
				vsandmsdata[3] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[3], vandmdataN[3]);
				vsandmsdata[4] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[4], vandmdataN[4]);
				vsandmsdata[5] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[5], vandmdataN[5]);
				vsandmsdata[6] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[6], vandmdataN[6]);
				vsandmsdata[7] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[7], vandmdataN[7]);
				vsandmsdata[8] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[8], vandmdataN[8]);
				vsandmsdata[9] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[9], vandmdataN[9]);
				vsandmsdata[10] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[10], vandmdataN[10]);
				vsandmsdata[11] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[11], vandmdataN[11]);
				vsandmsdata[12] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[12], vandmdataN[12]);
				vsandmsdata[13] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[13], vandmdataN[13]);
				vsandmsdata[14] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[14], vandmdataN[14]);
				vsandmsdata[15] = MEMCAP2_CREATEVBUFFERSTRUCT(vandmdataM[15], vandmdataN[15]);
	
				MEMCAP2_WRITETOBUFFER_VDATASANDVMASKS(index, buffer, vsandmsdata, 0);	
			}
		}
	}
	return;	
}

