					unsigned int tdepth5 = capsule_offset + depths[5];
					unsigned int tdepth6 = capsule_offset + depths[6];
					unsigned int tdepth7 = capsule_offset + depths[7];
 	
					if(i < localcapsule[tdepth0].value){ enx[0] = true; } else { enx[0] = false; }
