					if(stage==0){ FIRSTSOURCEPARTITION = PARTITIONP2_CHKPT[edgebankID][v_chunkid]; LASTSOURCEPARTITIONS = PARTITIONP2_CHKPT[edgebankID][v_chunkid+1]; } 
