void acts_all::MEMACCESSP2_SPL_CKSreadvmaskschunks(bool_type enable, uint512_dt * kvdram, unit1_type vmaskBITS[VMASK_PACKINGSIZE][BLOCKRAM_VMASK_SIZE], keyvalue_vbuffer_t buffer[VDATA_PACKINGSIZE][BLOCKRAM_VDATA_SIZE], batch_type basevoffset_kvs, batch_type voffset_kvs, batch_type vsz_kvs, globalparams_t globalparams){				
	unsigned int depth = globalparams.NUM_REDUCEPARTITIONS * globalparams.SIZEKVS2_REDUCEPARTITION;
	unsigned int bdepth = vsz_kvs;
	unsigned int depth_i = 0;
	unsigned int bdepth_i = 0;
	unit1_type vmdata[VECTOR2_SIZE];
	unit1_type vmdata2[VECTOR2_SIZE];
	#pragma HLS ARRAY_PARTITION variable=vmdata complete
	#pragma HLS ARRAY_PARTITION variable=vmdata2 complete
	unit1_type tempvmaskBITS[VMASK_PACKINGSIZE][BLOCKRAM_VMASK_SIZE]; // CRITICAL REMOVEME.
	#pragma HLS array_partition variable = tempvmaskBITS
	int limit = 0;
	
	for(unsigned int s=0; s<NUM_PEs; s++){
		#ifdef _DEBUGMODE_KERNELPRINTS
		cout<<"MEMACCESSP2_SPL_CKSreadvmaskschunks:: size loaded @ s("<<s<<"): voffset_kvs: "<<voffset_kvs<<", sz: "<<(vsz_kvs * VECTOR2_SIZE)<<endl;
		#endif 
		MEMACCESSP2_SPL_readvmasks_slide(enable, s, kvdram, tempvmaskBITS, buffer, basevoffset_kvs, depth_i, voffset_kvs, bdepth_i, vsz_kvs); 
		depth_i += depth;
		bdepth_i += bdepth;
	}
	
	for(unsigned int i=0; i<DOUBLE_BLOCKRAM_SIZE; i++){
MEMCAP2_READFROMBUFFER_VMASKS(i, tempvmaskBITS, vmdata, 0);
		#ifdef _DEBUGMODE_KERNELPRINTS3
		if(i<limit){ cout<<"MEMACCESSP2_SPL_CKSreadvmaskschunks:BEFORE:["<<i<<"]: "; for(unsigned int v=0; v<VECTOR2_SIZE; v++){ cout<<vmdata[v]<<", "; } cout<<endl; }
		#endif 
		MEMACCESSP2_SPL_RearrangeLayoutVM(voffset_kvs + i, vmdata, vmdata2);
		#ifdef _DEBUGMODE_KERNELPRINTS3
		if(i<limit){ cout<<"MEMACCESSP2_SPL_CKSreadvmaskschunks:AFTER:["<<i<<"]: "; for(unsigned int v=0; v<VECTOR2_SIZE; v++){ cout<<vmdata2[v]<<", "; } cout<<endl; }
		#endif 
MEMCAP2_WRITETOBUFFER_VMASKS(i, vmaskBITS, vmdata2, 0);
	}
	
	#ifdef _DEBUGMODE_KERNELPRINTS
	cout<<"+++ MEMACCESSP2_SPL_CKSreadvmaskschunks:: total size (bits) loaded: "<<vsz_kvs * NUM_PEs * VECTOR2_SIZE * 32<<endl;
	#endif
	return;	
}

